  const clicked = () => {
    console.log('The button was clicked')
  }

  const inputCall = () => {
    const inputed = this.value
  }


  const checkValue = (value) => {

    if (value == 0) 
    {
    return alert('Erro: o valor de custo não pode ser igual a 0.');
    } else if (isNaN(value)) 
    {
    return alert('Erro: o valor precisa ser um número');
    } else if (typeof (value) === ('number'))
    {
    return alert('sucesso');
    } else 
    {
    alert('Erro inesperado.');
    }
  }

  return (
    <>
      <label>Insira o custo:
      <input type = "text" onChange={() => inputCall()}>
      </input>
      </label>
    <>    
    </>
      <button value="Calcular" onClick={() => checkValue(inputed)}>Calcular</button> 
    </>
  )
}
