function App() {
  let store
  const storesMarkup = {
    coisasMilRod: 2.5,
    havaianasRod: 2.5,
    havaianasBon: 2.3,
    roupasRod: 2.3
  }

  return (
    <>
      <SelectBusinessBtn/>
      <InputCost/>
      <Calculate/>
    </>
  )
}



const FinalPrice = (
  custo,
  loja = String,
  margemHVNRod = 2.5,
  margemHVNBon = 2.3,
  margemCM = 2.5,
  margemRP = 2.3) => {


  switch (loja = 'HVN') {
    case 'HVN':
      let msg = ('Preço de custo: R$' + custo + '\nPreço de venda Rodoviária: R$' + (custo * margemHVNRod) + '\nPreço de venda Bonsucesso: R$' + (custo * margemHVNBon));
      msg = msg.replace(".", ",")
      return (
        <h1>
        {MsgBox(msg)}
        </h1>
      )
    case 'CM':
      msg = ('Preço de custo: R$' + custo + '\nPreço de venda Rodoviária: R$' + (custo * margemCM))
      msg = msg.replace(".", ",")
      return MsgBox(msg)
    case 'RP':
      msg = ('Preço de custo: R$' + custo + '\nPreço de venda Rodoviária: R$' + (custo * margemRP))
      msg = msg.replace(".", ",")
      return MsgBox(msg)
    }
  }
const MsgBox = (String) => {
  return(
    <h1>
      {String}
    </h1>
  )
}
const InputCost = () => {
  return (
    <input placeholder="Insira o preço de custo"></input>
  )
}

const Calculate = () => {
  return (
  <input onClick={FinalPrice()} type="submit" value="Calcular"></input>
  )
}
const SelectBusinessBtn = () => {
  const selectBusiness = (props) => {
    const business = [
      "Coisas Mil",
      "Havaianas Rodoviária",
      "RP Modas"
    ]
    return (business[props])
  }

  return (
    <>
      <input onClick={store = selectBusiness(1)} type="submit" value="Coisas Mil"></input>

      <input onClick={selectBusiness(2)} type="submit" value="Havaianas"></input>

      <input onClick={selectBusiness(3)} type="submit" value="RP Modas"></input>
    </>
  )
}




export default App;
