function App() {
  return (
    <>
      <CMBtn/>
      <InputCost/>
      <Calculate/>

    </>
  )
}


const FinalPrice = (cost) => {

  const markup = {
    CMRod: 2.5,
    HVNRod: 2.5,
    HVNBon: 2.3,
    RPRod: 2.3
  }

  let business = 'HVN'

  switch (business) {
    case 'HVN':
      return (
        <>
          <p>
            Preço de custo: R${cost}
          </p>
          <p>
            Preço de venda Rodoviária: R${cost * markup.HVNRod}
          </p>
          <p>
            Preço de venda Bonsucesso: R${cost * markup.HVNBon}
          </p>
        </>
      )
    case 'CM':
      return (
        <>
        <p>
          Preço de custo: R${cost}
        </p>
        <p>
          Preço de venda Rodoviária: R${cost * markup.CMRod}
        </p>
      </>
      )
    case 'RP':
      return (
        <>
        <p>
          Preço de custo: R${cost}
        </p>
        <p>
          Preço de venda Rodoviária: R${cost * markup.RPRod}
        </p>
      </>
      )
    }
  }

const InputCost = () => {
let cost

  return (
    <form>
      <label>Insira o custo:
        <input
          value={cost}
        />
      </label>
    </form>
  )
}

const Calculate = () => {
  return (
  <input onClick={FinalPrice()} type="submit" value="Calcular"></input>
  )
}
const CMBtn = () => {
  return (
    <>
      <input onClick={() => 'CM'} type="submit" value="Coisas Mil"></input>
    </>
  )
}


