import { useState } from "react";

export const CalculateBtn = () => {
  const [cost, setCost] = useState("")




  const checkValue = () => {

    if (cost == 0) 
    {
    return alert('Erro: o valor de custo não pode ser igual a 0.');
    } else if (isNaN(cost)) 
    {
    return alert('Erro: o valor precisa ser um número');
    } else if (typeof (cost) === ('number'))
    {
    return alert('sucesso');
    } else 
    {
    alert('Erro inesperado.');
    }
  }

  return (
    <>
      <label>Insira o custo:
      <input value={cost} onChange={() => this.setCost(cost)}>
      </input>
      </label>
    <>    
    </>
      <button value="Calcular" onClick={() => checkValue()}>Calcular</button> 
    </>
  )
}
