function App() {
let businessSelected
  return (
    <>
      <CMBtn/>
      <InputCost/>
      <Calculate/>
    </>
  )
}



const FinalPrice = (props) => {
  const markup = {
    CMRod: 2.5,
    HVNRod: 2.5,
    HVNBon: 2.3,
    RPRod: 2.3
  }


  switch (businessSelected) {
    case 'HVN':
      return (
        <>
          <p>
            Preço de custo: R${props.custo}
            Preço de venda Rodoviária: R${props.custo * markup.HVNRod}
            Preço de venda Bonsucesso: R${props.custo * markup.HVNBon}
          </p>
        </>
      )
    case 'CM':
      return (
        <>
        <p>
          Preço de custo: R${props.custo}
          Preço de venda Rodoviária: R${props.custo * markup.CMRod}
        </p>
      </>
      )
    case 'RP':
      return (
        <>
        <p>
          Preço de custo: R${props.custo}
          Preço de venda Rodoviária: R${props.custo * markup.RPRod}
        </p>
      </>
      )
    }
  }

const InputCost = () => {
  return (
    <input placeholder="Insira o preço de custo"></input>
  )
}

const Calculate = () => {
  return (
  <input onClick={FinalPrice()} type="submit" value="Calcular"></input>
  )
}
const CMBtn = () => {
  let businessSelected

  return (
    <>
      <input onClick={() => {businessSelected = 'CM'}} type="submit" value="Coisas Mil"></input>
    </>
  )
}


