export const CalculateBtn = () => {
  const clicked = () => {
    console.log('The button was ${clicked} ')
  }

  const checkValue = (value) => {

    if (value == 0) 
    {
    return alert('Erro: o valor de custo não pode ser igual a 0.');
    } else if (isNaN(value)) 
    {
    return alert('Erro: o valor precisa ser um número');
    } else if (typeof (value) === ('number'))
    {
    return FinalPrice(value, 'CM');
    } else 
    {
    alert('Erro inesperado.');
    }
  }


  const CalculateBtn = () => {

    <input onClick={() => checkValue()} 

    type="submit" value="Calcular"></input>
  }
}
